import React from "react";
import CartItem from "./CartItem";
import "./cartStyle.css";

class CartExample extends React.Component {
  state = {
    cartItems: [
      { id: 1, name: "Pizza", qty: 1, price: 100, total: 0 },
      { id: 2, name: "Burger", qty: 0, price: 50, total: 0 },
      { id: 3, name: "Fries", qty: 4, price: 80, total: 0 },
      { id: 4, name: "Sandwich", qty: 0, price: 150, total: 0 },
    ],
  };

  render() {
    return (
      <React.Fragment>
        <h1>Cart Example in React JS</h1>
        <table border="1">
          <tr>
            <th>Item</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Total</th>
            <th>Action</th>
          </tr>
          {this.state.cartItems.map((cart) => (
            <CartItem
              nameItem={cart.name}
              qty={cart.qty}
              price={cart.price}
              key={cart.id}
              total={cart.total}
            />
          ))}
          <tr>
            <th colSpan="2">Total Amount</th>
            <th>
              {this.state.cartItems.reduce(
                (tot, cart) => tot + cart.qty * cart.price,
                0
              )}
            </th>
            <th>Quantity</th>
            <th>0</th>
          </tr>
        </table>
      </React.Fragment>
    );
  }
}

export default CartExample;
